// @flow
import Chrome from './chrome/chrome';
import Launcher from './chrome/launcher';

import DevToolsInterface from './chrome/devtools';

import type { chrome as chromeType, devToolsProtocolClient } from './chrome/types/chrome';
import type { launcher as launcherType } from './chrome/types/launcher';

/**
 * @type {Chaldeas}
 */
export default class Chaldeas {
  chrome: chromeType;
  launcher: launcherType;

  /**
   */
  constructor(chrome: Chrome, launcher: Launcher) {
    this.chrome = chrome;
    this.launcher = launcher;
  }

  /**
   * constructor alias,
   */
  static new(chromeOption?: Object, launchOption?: Object): Chaldeas {
    const chrome = new Chrome(chromeOption);
    const launcher = new Launcher(launchOption);

    return new Chaldeas(chrome, launcher);
  }

  /**
   * Chrome Launch with Headlessmode and Connect devtools protocol
   */
  async dispatch() {
    if (!this.launcher.isLaunched()) {
      await this.launcher.start();
    }

    await this.chrome.connectDevToolsProtocolClient();
  }

  /**
   * fetch chromedevtools protocol client
   */
  async fetchProtocol(): Promise<devToolsProtocolClient> {
    const procotol = await this.dispatch().then(async () => {
      const client = await this.chrome.fetchProtocol();
      return client;
    }).catch(e => this.terminate().then(() => {
      throw e;
    }));

    return procotol;
  }

  /**
 * get DevToolsInterface class
 */
  async getDevtoolsInterface(): Promise<DevToolsInterface> {
    const client = await this.fetchProtocol();
    return new DevToolsInterface(client);
  }

  /**
   * terminate chrome and disconnect devToolsProtocol
   */
  async terminate() {
    await this.chrome.terminate();
    await this.launcher.terminate();
  }
}

// @flow
import CDP from 'chrome-remote-interface';
import Defaults from 'chrome-remote-interface/lib/defaults';

import type { chromeOptionProperties, devToolsProtocolClient } from './types/chrome';

/**
 * chrome default option
 */
export const defaultOption = {
  host: Defaults.HOST,
  port: Defaults.PORT,
  secure: true,
};

/**
 * Convert to object corresponding to chrome launcher option.
 */
export function mergeDefaultOption(option?: Object): chromeOptionProperties {
  return Object.assign({}, defaultOption, option);
}

/**
 * @type {Chrome}
 */
export default class Chrome {
  option: chromeOptionProperties;
  client: devToolsProtocolClient;

  /**
   * chrome class constructor
   */
  constructor(options?: Object) {
    this.option = mergeDefaultOption(options);
  }

  /**
   * try connect at chrome devtools protocol
   */
  async connectDevToolsProtocolClient(): Promise<Chrome> {
    if (!this.client) {
      const [tab] = await CDP.List();
      this.client = await CDP({ host: this.option.host, target: tab });
    }

    return this;
  }

  /**
   * fetch devtools-protocol client
   */
  async fetchProtocol(): Promise<devToolsProtocolClient> {
    await this.connectDevToolsProtocolClient().catch(e => this.terminate().then(() => {
      throw e;
    }));

    return this.client;
  }

  /**
   * terminate chrome.
   */
  async terminate(): Promise<Chrome> {
    if (this.client) {
      await this.client.close();
      delete this.client;
    }
    return this;
  }
}

// @flow

import type { devToolsProtocolClient } from './types/chrome';
import type { Page } from './types/devtools/page';
import type { Network } from './types/devtools/network';
import type { DOM } from './types/devtools/dom';
import type { Runtime } from './types/devtools/runtime';
import type { Emulation } from './types/devtools/emulation';
import type { Input } from './types/devtools/input';
import type { Target } from './types/devtools/target';
import type { Security } from './types/devtools/security';

/**
 * @type {DevTools}
 */
export default class DevTools {
  client: devToolsProtocolClient;

  /**
   * @param {devToolsProtocolClient} client
   */
  constructor(client: devToolsProtocolClient) {
    this.client = client;
  }

  async PageDomain(): Promise<Page> {
    await this.client.Page.enable();
    return this.client.Page;
  }

  async NetworkDomain(): Promise<Network> {
    await this.client.Network.enable();
    return this.client.Network;
  }

  async DOMDomain(): Promise<DOM> {
    await this.client.DOM.enable();
    return this.client.DOM;
  }

  async RuntimeDomain(): Promise<Runtime> {
    return this.client.Runtime;
  }

  async EmulationDomain(): Promise<Emulation> {
    return this.client.Emulation;
  }

  async InputDomain(): Promise<Input> {
    return this.client.Input;
  }

  async TargetDomain(): Promise<Target> {
    return this.client.Target;
  }

  async SecurityDomain(enabled?: boolean): Promise<Security> {
    if (enabled) {
      await this.client.Security.enable();
    }

    return this.client.Security;
  }
}

// @flow
import { ChromeLauncher } from 'lighthouse/lighthouse-cli/chrome-launcher';

import type { launchConfig, chromeLauncherType } from './types/launcher';

/**
 * chrome headless mode launch default options
 */
export const defaultConfig = {
  port: 9222,
  autoSelectChrome: true,
  additionalFlags: [
    '--window-size=412,732',
    '--disable-gpu',
    '--headless',
  ],
};

/**
 * Convert to object corresponding to chrome launcher option.
 */
export function mergeConfig(config?: Object): launchConfig {
  return Object.assign({}, defaultConfig, config);
}

/**
 * @type {Launcher}
 */
export default class Launcher {
  /**
   * launcher config
   */
  config: launchConfig;

  /**
   * lighthouse/lighthouse-cli/chrome-launcher instance
   */
  chromeLauncher: chromeLauncherType;

  /**
   */
  constructor(config?: Object): void {
    this.config = mergeConfig(config);
    this.chromeLauncher = new ChromeLauncher(this.config);
  }

  /**
   * check chrome is launched?
   */
  isLaunched(): boolean {
    if (this.chromeLauncher.chrome) {
      return true;
    }

    return false;
  }

  /**
   * chrome reboot method.
   */
  async reboot(): Promise<{}> {
    if (!this.isLaunched()) {
      const s = await this.start();
      return s;
    }

    await this.terminate();
    const r = await this.start();
    return r;
  }

  /**
   * chrome is terminate
   */
  async terminate(): Promise<{}> {
    return this.chromeLauncher.kill();
  }

  /**
   * Launch Chrome with Background.
   */
  async start(): Promise<{}> {
    return this.chromeLauncher.run().catch(err => this.terminate().then(() => {
      throw err;
    }));
  }
}

// @flow
import type { Page as page } from './devtools/page';
import type { Network as network } from './devtools/network';
import type { DOM as dom } from './devtools/dom';
import type { Runtime as runtime } from './devtools/runtime';
import type { Emulation as emulation } from './devtools/emulation';
import type { Input as input } from './devtools/input';
import type { Target as target } from './devtools/target';
import type { Security as security } from './devtools/security';

import ChromeClaass from './../chrome';

export type chromeDefaultConfig = {
  host: string;
  port: number;
  secure: boolean;
};

export type chromeOptionProperties = {
  host: string;
  port: number;
  secure: boolean;
  target: Function;
};

/**
 * @type {Object}
 */
export type devToolsProtocolClient = {
  Protocol: Promise<{}>;
  List: Promise<{}>;
  New: Promise<{}>;
  Activate: Promise<{}>;
  Close: Promise<{}>;
  Version: Promise<{}>;

  Page: page;
  Network: network;
  DOM: dom;
  Runtime: runtime;
  Input: input;
  Emulation: emulation;
  Target: target;
  Security: security;

  close(): void;
};

export type chrome = {
  option: chromeOptionProperties;
  client: devToolsProtocolClient;

  constructor(option?: Object): void;
  connectDevToolsProtocolClient(): Promise<ChromeClaass>;
  fetchProtocol(): Promise<devToolsProtocolClient>;
  terminate(): Promise<ChromeClaass>;
};

// @flow
// from: https://chromedevtools.github.io/devtools-protocol/tot/Debugger/

export type SearchMatch = {
  lineNumber: Number,
  lineContent: string
};

// @flow
// from : https://chromedevtools.github.io/devtools-protocol/tot/DOM/

import type { FrameId } from './page';
import type { RemoteObjectId, RemoteObject } from './runtime';

/**
 * Unique DOM node identifier.
 *
 * @type {Number}
 */
export type NodeId = Number;

/**
 * Unique DOM node identifier used to reference a node
 * that may not have been pushed to the front-end
 *
 * @type {Number}
 */
export type BackendNodeId = Number;

/**
 * Backend node with a friendly name.
 *
 * @type {Object}
 */
export type BackendNode = {
  nodeType: Number,
  nodeName: string,
  backendNodeId: BackendNodeId,
};

/**
 * Pseudo element type.
 *
 * @type {string}
 */
export type PseudoType = "first-line" | "first-letter" | "before" |
                         "after" | "backdrop" | "selection" | "first-line-inherited" |
                         "scrollbar" | "scrollbar-thumb" | "scrollbar-button" |
                         "scrollbar-track" | "scrollbar-track-piece" | "scrollbar-corner" |
                         "resizer" | "input-list-button";

/**
 * Shadow root type.
 *
 * @type {string}
 */
export type ShadowRootType = "user-agent" | "open" | "closed";

/**
 * DOM interaction is implemented in terms of mirror objects that represent the actual DOM nodes.
 * DOMNode is a base node mirror type.
 *
 * @type {Object}
 */
export type Node = {
  nodeId: NodeId,
  parentId?: NodeId,
  backendNodeId: BackendNodeId,
  nodeType: Number,
  nodeName: string,
  localName: string,
  nodeValue: string,
  childNodeCount?: Number,
  children?: Array<Node>,
  attributes?: Array<string>,
  documentURL?: string,
  baseURL?: string,
  publicId?: string,
  systemId?: string,
  internalSubset?: string,
  xmlVersion?: string,
  name?: string,
  value?: string,
  pseudoType?: PseudoType,
  shadowRootType?: ShadowRootType,
  frameId?: FrameId,
  contentDocument?: Node,
  shadowRoots?: Array<Node>,
  templateContent?: Node,
  pseudoElements?: Array<Node>,
  importedDocument?: Node,
  distributedNodes?: Array<BackendNode>,
  isSVG?: boolean,
};

/**
 * A structure holding an RGBA color.
 *
 * @type {Object}
 */
export type RGBA = {
  r: Number,
  g: Number,
  b: Number,
  a?: Number,
};

/**
 * An array of quad vertices, x immediately followed by y for each point, points clock-wise.
 *
 * @type {Array}
 */
export type Quad = Array<mixed>;

/**
 * Rectangle
 *
 * @type {Object}
 */
export type Rect = {
  x: Number,
  y: Number,
  width: Number,
  height: Number
};

/**
 * CSS Shape Outside details
 *
 * @type {Object}
 */
export type ShapeOutsideInfo = {
  bouds: Quad,
  shape: Array<mixed>,
  marginShape: Array<mixed>,
};

/**
 * Box model
 *
 * @type {Object}
 */
export type BoxModel = {
  content: Quad,
  padding: Quad,
  border: Quad,
  margin: Quad,
  width: Quad,
  height: Quad,
  shapeOutside: ShapeOutsideInfo,
};

/**
 * DOM events and methods
 *
 * @type {Object}
 */
export type DOM = {
  /**
   * Enables DOM agent for the given page.
   *
   * @return {Promise}
   */
  enable(): Promise<>;

  /**
   * Disables DOM agent for the given page.
   *
   * @return {Promise}
   */
  disable(): Promise<>;

  /**
   * Returns the root DOM node (and optionally the subtree) to the caller.
   *
   * @param {Object}
   * @return {Promise} Promise<{root: Node}>
   */
  getDocument(arg?: {depth?: Number, pierce?: boolean}): Promise<{root: Node}>;

  /**
   * Returns the root DOM node (and optionally the subtree) to the caller.
   *
   * @param {Object}
   * @return {Promise} Promise<{root: Node}>
   */
  getFlattenedDocument(arg?: {death?: Number, pierce?: boolean}): Promise<{nodes: Array<Node>}>;

  /**
   * Collects class names for the node with given id and all of it's child nodes.
   *
   * @param {Object}
   * @return {Promise} Promise<{classNames: Array<string>}>
   */
  collectClassNamesFromSubtree(arg: {nodeId: NodeId}): Promise<Array<string>>;

  /**
   * Requests that children of the node with
   * given id are returned to the caller in form of setChildNodes events
   * where not only immediate children are retrieved, but all children down to the specified depth.
   *
   * @param {Object}
   * @return {Promise}
   */
  requestChildNodes(arg: {nodeId: NodeId, depth?: Number, pierce?: boolean}): Promise<>;

  /**
   * Executes querySelector on a given node.
   *
   * @param {Object}
   * @return {Promise} Promise<{nodeId: NodeId}>
   */
  querySelector(arg: {
    nodeId: NodeId,
    selector: string
  }): Promise<{nodeId: NodeId}>;

  /**
   * Executes querySelectorAll on a given node.
   *
   * @param {Object}
   * @return {Promise} Promise<{nodeIds: NodeId}>
   */
  querySelectorAll(arg: {
    nodeId: NodeId,
    selector: string
  }): Promise<{nodeIds: Array<NodeId>}>;

  /**
   * Sets node name for a node with given id.
   *
   * @param {Object}
   * @return {Promise} Promise<{nodeId: NodeId}>
   */
  setNodeName(arg: {nodeId: NodeId, name: string}): Promise<{nodeId: NodeId}>;

  /**
   * Sets node value for a node with given id.
   *
   * @param {Object}
   * @return {Promise}
   */
  setNodeValue(arg: {nodeId: NodeId, name: string}): Promise<>;

  /**
   * Removes node with given id.
   *
   * @param {Object}
   * @return {Promise}
   */
  removeNode(arg: {nodeId: NodeId}): Promise<>;

  /**
   * Sets attribute for an element with given id.
   *
   * @param {Object}
   * @return {Promise}
   */
  setAttributeValue(arg: {nodeId: NodeId, name: string, value: string}): Promise<>;

  /**
   * Sets attributes on element with given id.
   * This method is useful when user edits some existing
   * attribute value and types in several attribute name/value pairs.
   *
   * @param {Object}
   * @return {Promise}
   */
  setAttributesAsText(arg: {nodeId: NodeId, text: string, name?: string}): Promise<>;

  /**
   * Removes attribute with given name from an element with given id.
   *
   * @param {Object}
   * @return {Promise}
   */
  removeAttribute(arg: {nodeId: NodeId, name: string}): Promise<>;

  /**
   * Returns node's HTML markup.
   *
   * @param {Object}
   * @return {Promise} Promise<{nodeHTML: string}>
   */
  getOuterHTML(arg: {nodeId: NodeId}): Promise<{outerHTML: string}>;

  /**
   * Sets node HTML markup, returns new node id.
   *
   * @param {Object}
   * @return {Promise}
   */
  setOuterHTML(arg: {nodeId: NodeId, outerHTML: string}): Promise<>;

  /**
   * Searches for a given string in the DOM tree.
   * Use getSearchResults to access search results or cancelSearch to end this search session.
   *
   * @param {Object}
   * @return {Promise} Promise<{searchid: string, resultCount: Number}>
   */
  performSearch(arg: {
    query: string, includeUserAgentShadowDOM?: boolean
  }): Promise<{searchId: string, resultCount: Number}>;

  /**
   * Returns search results
   * from given fromIndex to given toIndex from the sarch with the given identifier.
   *
   * @param {Object}
   * @return {Promise} Promise<{nodeIds: Array<NodeId>}>
   */
  getSearchResults(arg: {
    searchId: string,
    fromIndex: Number,
    toIndex: Number
  }): Promise<{nodeIds: Array<NodeId>}>;

  /**
   * Discards search results from the session with the given id.
   * getSearchResults should no longer be called for that search
   *
   * @param {Object}
   * @return {Promise}
   */
  discardSearchResults(arg: {searchId: string}): Promise<>;

  /**
   * Requests that the node is sent to the caller given the JavaScript node object reference.
   * All nodes that form the path from the node to the root are also sent to
   * the client as a series of setChildNodes notifications.
   *
   * @param {Object}
   * @return {Promise}
   */
  requestNode(arg: {objectId: RemoteObjectId}): Promise<{nodeId: NodeId}>;

  /**
   * Requests that the node is sent to the caller given its path.
   *
   * @param {Object}
   * @return {Promise}
   */
  pushNodeByPathToFrontend(arg: {path: string}): Promise<{nodeId: NodeId}>;

  /**
   * Requests that a batch of nodes is sent to the caller given their backend node ids.
   *
   * @param {Object}
   * @return {Promise}
   */
  pushNodesByBackendIdsToFrontend(arg: {
    backendNodeIds: Array<BackendNodeId>
  }): Promise<{nodeIds: Array<NodeId>}>;

  /**
   * Enables console to refer to the node with given id via
   * $x (see Command Line API for more details $x functions).
   *
   * @param {Object}
   * @return {Promise}
   */
  setInspectedNode(arg: {nodeId: NodeId}): Promise<>;

  /**
   * Resolves JavaScript node object for given node id.
   *
   * @param {Object}
   * @return {Promise}
   */
  resolveNode(arg: {nodeId: NodeId, objectGroup?: string}): Promise<{object: RemoteObject}>;

  /**
   * Returns attributes for the specified node.
   *
   * @param {Object}
   * @return {Promise} Promise<{attributes: Array<string>}>
   */
  getAttributes(arg: {nodeId: NodeId}): Promise<{attributes: Array<string>}>;

  /**
   * Creates a deep copy of the specified node and places
   * it into the target container before the given anchor.
   *
   * @param {Object}
   * @return {Promise}
   */
  copyTo(arg: {
    nodeId: NodeId, targetNodeId: NodeId, insertBeforeNodeId?: NodeId
  }): Promise<{nodeId: NodeId}>;


 /**
  * @param {Object}
  * @return {Promise}
  */
  moveTo(arg: {
   nodeId: NodeId, targetNodeId: NodeId, insertBeforeNodeId?: NodeId
 }): Promise<{nodeId: NodeId}>;

  /**
   * Undoes the last performed action.
   *
   * @return {Promise}
   */
  undo() : Promise<>;

  /**
   * Re-does the last undone action.
   *
   * @return {Promise}
   */
  redo(): Promise<>;

  /**
   * Marks last undoable state.
   *
   * @return {Promise}
   */
  markUndoableState(): Promise<>;

  /**
   * Focuses the given element.
   *
   * @param {Object}
   * @return {Promise}
   */
  focus(arg: {nodeId: NodeId}): Promise<>;

  /**
   * Sets files for the given file input element.
   *
   * @param {Object}
   * @return {Promise}
   */
  setFileInputFiles(arg: {nodeId: NodeId, files: Array<string>}): Promise<>;

  /**
   * Box model.
   *
   * @param {Object}
   * @return {Promise}
   */
  getBoxModel(arg: {nodeId: NodeId}): Promise<{model: BoxModel}>;

  /**
   * Returns node id at given location.
   *
   * @param {Object}
   * @return {Promise}
   */
  getNodeForLocation(arg: {
    x: Number, y: Number,
    includeUserAgentShadowDOM?: boolean
  }): Promise<{nodeId: NodeId}>;

  /**
   * Returns the id of the nearest ancestor that is a relayout boundary.
   *
   * @param {Object}
   * @return {Promise}
   */
  getRelayoutBoundary(arg: {nodeId: NodeId}): Promise<{nodeId: NodeId}>;

  /* events */

  /**
   * Fired when Document has been totally updated. Node ids are no longer valid.
   *
   * @param {Function}
   */
  documentUpdated(callback: Function): void;

  /**
   * Fired when backend wants to provide client with the missing DOM structure.
   * This happens upon most of the calls requesting node ids.
   *
   * @param {Function}
   */
  setChildNodes(callback: (parentId: NodeId, nodes: Array<Node>) => void): void;


  /**
   * Fired when Element's attribute is modified.
   *
   * @param {Function}
   */
  attributeModified(callback: (
    nodeId: NodeId, name: string, value: string
  ) => void): void;

  /**
   * Fired when Element's attribute is removed.
   *
   * @param {Function}
   */
  attributeRemoved(callback: (
    nodeId: NodeId, name: string
  ) => void): void;

  /**
   * Fired when Element's inline style is modified via a CSS property modification.
   *
   * @param {Function}
   */
  inlineStyleInvalidated(callback: (
    nodeIds: Array<NodeId>
  ) => void): void;

  /**
   * Mirrors DOMCharacterDataModified event.
   *
   * @param {Function}
   */
  characterDataModified(callback: (
    nodeId: NodeId, characterData: string
  ) => void): void;

  /**
   * Fired when Container's child node count has changed.
   *
   * @param {Function}
   */
  childNodeCountUpdated(callback: (
    nodeId: NodeId, childNodeCount: Number
  ) => void): void;

  /**
   * Mirrors DOMNodeInserted event.
   *
   * @param {Function}
   */
  childNodeInserted(callback: (
    parentNodeId: NodeId,
    previousNodeId: NodeId,
    node: Node
  ) => void): void;


  /**
   * Mirrors DOMNodeRemoved event.
   *
   * @param {Function}
   */
  childNodeRemoved(callback: (parentNodeId: NodeId, nodeId: NodeId) => void): void;

  /**
   * Called when shadow root is pushed into the element.
   *
   * @param {Function}
   */
  shadowRootPushed(callback: (hostId: NodeId, root: Node) => void): void;

  /**
   * Called when shadow root is popped from the element.
   *
   * @param {Function}
   */
  shadowRootPopped(callback: (hostId: NodeId, root: Node) => void): void;

  /**
   * Called when a pseudo element is added to an element.
   *
   * @param {Function}
   */
  pseudoElementAdded(callback: (parentNodeId: NodeId, pseudoElement: Node) => void): void;

  /**
   * Called when a pseudo element is removed from an element.
   *
   * @param {Function}
   */
  pseudoElementRemoved(callback: (parentNodeId: NodeId, pseudoElement: Node) => void): void;

  /**
   * Called when distrubution is changed.
   *
   * @param {Function}
   */
  distributedNodesUpdated(callback: (
    insertionPointId: NodeId, distributedNodes: Array<Node>
  ) => void): void;
};

// @flow
// from: https://chromedevtools.github.io/devtools-protocol/tot/Emulation/

import type { RGBA } from './dom';

/**
 * Screen orientation.
 *
 * @type {Object}
 */
export type ScreenOrientation = {
  type: "portraitPrimary" | "portraitSecondary" | "landscapePrimary" | "landscapeSecondary",
  angle: Number,
};

/**
 * advance: If the scheduler runs out of immediate work,
 * the virtual time base may fast forward to allow the next delayed task (if any) to run;
 * pause: The virtual time base may not advance;
 * pauseIfNetworkFetchesPending: The virtual time base may not advance
 * if there are any pending resource fetches.
 *
 * @type {string}
 */
export type VirtualTimePolicy = "advance" | "pause" | "pauseIfNetworkFetchesPending";

/**
 * Emultation Domain
 *
 * @type {Object}
 */
export type Emulation = {
  /**
   * Overrides the values of device screen dimensions
   * (window.screen.width, window.screen.height, window.innerWidth, window.innerHeight,
   * and "device-width"/"device-height"-related CSS media query results).
   *
   * @param {Object}
   * @return {Promise}
   */
  setDeviceMetricsOverride(arg: {
    width: Number,
    height: Number,
    deviceScaleFactor: Number,
    mobile: boolean,
    fitWindow: boolean,
    scale?: Number,
    offsetX?: Number,
    offsetY?: Number,
    screenWidth?: Number,
    screenHeight?: Number,
    positionX?: Number,
    positionY?: Number,
    screenOrientation?: ScreenOrientation
  }): Promise<>;

  /**
   * Clears the overriden device metrics.
   *
   * @return {Promise}
   */
  clearDeviceMetricsOverride(): Promise<>;

  /**
   * Overrides the visible area of the page.
   * The change is hidden from the page,
   * i.e. the observable scroll position and page scale does not change.
   * In effect, the command moves the specified area of
   * the page into the top-left corner of the frame.
   *
   * @param {Object}
   * @return {Promise}
   */
  forceViewport(arg: {
    x: Number,
    y: Number,
    scale: Number
  }): Promise<>;

  /**
   * Resets the visible area of the page to the original viewport,
   * undoing any effects of the forceViewport command.
   *
   * @return {Promise}
   */
  resetViewport(): Promise<>;

  /**
   * Requests that page scale factor is reset to initial values.
   *
   * @return {Promise}
   */
  resetPageScaleFactor(): Promise<>;

  /**
   * Sets a specified page scale factor.
   *
   * @param {Object}
   * @return {Promise}
   */
  setPageScaleFactor(arg: { pageScaleFactor: Number }): Promise<>;

  /**
   * Resizes the frame/viewport of the page.
