  captureScreenshot(arg?: {
    format?: "jpeg" | "png",
    quality?: Number,
    fromSurface?: boolean
  }): Promise<{data: string}>;

  /**
   * Print page as PDF.
   *
   * @param {Object}
   * @return {Promise}
   */
  printToPDF(arg?: {
    landscape?: boolean,
    displayHeaderFooter?: boolean,
    printBackground?: boolean,
    scale?: Number,
    paperWidth?: Number,
    paperHeight?: Number,
    marginTop?: Number,
    marginBottom?: Number,
    marginLeft?: Number,
    marginRight?: Number,
    pageRanges?: string
  }): Promise<{data: string}>;

  /**
   * Starts sending each frame using the screencastFrame event.
   *
   * @param {Object}
   * @return {Promise}
   */
  startScreencast(arg?: {
    format?: "jpeg" | "png",
    quality?: Number,
    maxWidth?: Number,
    maxHeight?: Number,
    everyNthFrame?: Number
  }): Promise<>;

  /**
   * Stops sending each frame in the screencastFrame
   * @return {Promise}
   */
  stopScreencast(): Promise<>;

  /**
   * Acknowledges that a screencast frame has been received by the frontend.
   *
   * @param {Object}
   * @return {Promise}
   */
  screencastFrameAck(arg: {seccionId: Number}): Promise<>;

  /**
   * Accepts or dismisses a JavaScript initiated dialog (alert, confirm, prompt, or onbeforeunload).
   *
   * @param {Object}
   * @return {Promise}
   */
  handleJavaScriptDialog(arg: {accept: boolean, promptText?: string}): Promise<>;

  /**
   * @return {Promise} { url: string, errors: Array<AppManifestError>, data?: string}
   */
  getAppManifest(): Promise<{ url: string, errors: Array<AppManifestError>, data?: string}>;

  /**
   */
  requestAppBanner(): Promise<>;

  /**
   * Toggles navigation throttling
   * which allows programatic control over navigation and redirect response.
   *
   * @param {Object}
   * @return {Promise}
   */
  setControlNavigations(arg: {enable: boolean}): Promise<>;

  /**
   * Should be sent in response to a navigationRequested or a redirectRequested event,
   * telling the browser how to handle the navigation.
   *
   * @param {Object}
   * @return {Promise}
   */
  processNavigation(arg: {response: NavigationResponse, navigationId: Number}): Promise<>;

  /**
   * Returns metrics relating to the layouting of the page, such as viewport bounds/scale.
   *
   * @return {Promise}
   */
  getLayoutMetrics(): Promise<{
    layoutViewport: LayoutViewport,
    visualViewport: VisualViewport,
    contentSize: Rect
  }>;

  /**
   * Creates an isolated world for the given frame
   *
   * @param {Object}
   * @return {Promise}
   */
  createIsolatedWorld(arg: {
    frameId: FrameId,
    worldName?: string,
    grantUniveralAccess: boolean
  }): Promise<>;

  /* -- events -- */

  /**
   * @param {Function}
   */
  domContentEventFired(
    callback: (timestamp: Timestamp) => void
  ): void;

  /**
   * @param {Function}
   */
  loadEventFired(
    callback: (timestamp: Timestamp) => void
  ): void;

  /**
   * Fired when frame has been attached to its parent.
   *
   * @param {Function}
   */
  frameAttached(callback: (
    frameId: FrameId,
    parentFrameId: FrameId,
    stack?: StackTrace
  ) => void): void;

  /**
   * Fired once navigation of the frame has completed.
   * Frame is now associated with the new loader.
   *
   * @param {Function}
   */
  frameNavigated(callback: (frame: Frame) => void): void;

  /**
   * Fired when frame has been detached from its parent.
   *
   * @param {Function}
   */
  frameDetached(callback: (frameId: FrameId) => void): void;

  /**
   * Fired when frame has started loading.
   *
   * @param {Function} frameId
   */
  frameStartedLoading(callback: (frameId: FrameId) => void): void;

  /**
   * Fired when frame has stopped loading.
   *
   * @param {Function} frameId
   */
  frameStoppedLoading(callback: (frameId: FrameId) => void): void;

  /**
   * Fired when frame schedules a potential navigation.
   *
   * @param {Function}
   */
  frameScheduledNavigation(callback: (frameId: FrameId, delay: Number) => void): void;

  /**
   * Fired when frame no longer has a scheduled navigation.
   *
   * @param {Function}
   */
  frameClearedScheduledNavigation(callback: (frameId: FrameId) => void): void;

  /**
   * @param {Function}
   */
  frameResized(callback: Function): void;

  /**
   * Fired when a JavaScript initiated dialog
   * (alert, confirm, prompt, or onbeforeunload) has been closed.
   *
   * @param {Function}
   */
  javascriptDialogClosed(callback: (result: boolean) => void): void;

  /**
   * Compressed image data requested by the startScreencast.
   *
   * @param {Function}
   */
  screencastFrame(
    callback: (
      data: string,
      metadata: ScreencastFrameMetadata,
      sessionId: Number
    ) => void
  ): void;

  /**
   * Fired when the page with currently enabled screencast was shown or hidden .
   *
   * @param {Function}
   */
  screencastVisibilityChanged(callback: (visible: boolean) => void): void;

  /**
   * Fired when interstitial page was shown
   *
   * @param {Function}
   */
  interstitialShown(callback: Function): void;

  /**
   * Fired when interstitial page was hidden
   *
   * @param {Function}
   */
  interstitialHidden(callback: Function): void;

  /**
   * Fired when a navigation is started if navigation throttles are enabled.
   * The navigation will be deferred until processNavigation is called.
   *
   * @param {Function}
   */
  navigationRequested(
    callback: (
      isInMainFrame: boolean,
      isRedirect: boolean,
      navigationId: Number,
      url: string
    ) => void
  ): void;
};

// @flow
// from: https://chromedevtools.github.io/devtools-protocol/tot/Runtime/

import type { ConsoleType } from './console';

export type Timestamp = Number;

/**
 * @type {Object}
 */
export type CallFrame = Object;

/**
 * Call frames for assertions or error messages.
 *
 * @type {Object}
 */
export type StackTrace = {
  description?: string,
  CallFrames: Array<CallFrame>,
  parent?: StackTrace,
};


/**
 * Object type. Allowed values: object, function, undefined, string, number, boolean, symbol.

 * @type {string}
 */
export type RemoteObjectType = "object" | "function" | "undefined" | "string" | "number" | "boolean" | "symbol"

/**
 * Object subtype hint. Specified for object type values only
 *
 * @type {string}
 */
export type RemoteObjectSubType = "array" | "null" | "node" | "regexp" | "date" | "map" | "set" | "weakmap" | "weakset" | "iterator" | "generator" | "error" | "proxy" | "promise" | "typedarray";

/**
 * Primitive value which cannot be JSON-stringified.
 *
 * @type {string}
 */
export type UnserializableValue = "Infinity" | "NaN" | "-Infinity" | "-0";

/**
 * Unique object identifier.
 *
 * @type {string}
 */
export type RemoteObjectId = string;

/**
 * @type {string}
 */
export type PropertyObjectType = "object" | "function" | "undefined" | "string" | "number" | "boolean" | "symbol" | "accessor";

export type EntryPreview = {
};

/**
 * @type {Object}
 */
export type PropertyPreview = {
  name: string,
  type: PropertyObjectType,
  value: string,
  valuePreview?: {
    type: RemoteObjectType,
    subType?: RemoteObjectSubType,
    description?: string,
    overflow: boolean,
    properties: Array<PropertyPreview>,
    entries: Array<EntryPreview>,
  },
};

/**
 * @type {Object}
 */
export type ObjectPreview = {
  type: RemoteObjectType,
  subType?: RemoteObjectSubType,
  description?: string,
  overflow: boolean,
  properties: Array<PropertyPreview>,
  entries: Array<EntryPreview>,
};

/**
 * Mirror object referencing original JavaScript object.
 *
 * @type {Object}
 */
export type RemoteObject = {
  type: RemoteObjectType,
  subType?: RemoteObjectSubType,
  className?: string,
  value?: any,
  unserializableValue?: UnserializableValue,
  description?: string,
  objectId?: RemoteObjectId,
};


/**
 * Unique script identifier.
 *
 * @type {string}
 */
export type ScriptId = string;

/**
 * Id of an execution context.
 *
 * @type {Number}
 */
export type ExecutionContextId = Number;

/**
 * Detailed information about exception (or error)
 * that was thrown during script compilation or execution.
 *
 * @type {Object}
 */
export type ExceptionDetails = {
  exceptionId: Number,
  text: string,
  lineNumber: Number,
  columnNumber: Number,
  scriptId?: ScriptId,
  url?: string,
  stackTrace?: StackTrace,
  exception?: RemoteObject,
  executionContextId?: ExecutionContextId,
};

/**
 * Represents function call argument. Either remote object id objectId,
 * primitive value, unserializable primitive value or
 * neither of (for undefined) them should be specified.
 *
 * @type {Object}
 */
export type CallArgument = {
  value?: any,
  unserializableValue?: UnserializableValue,
  objectId?: RemoteObjectId,
};

/**
 * Object internal property descriptor. This property isn't normally visible in JavaScript code.
 *
 * @type {Object}
 */
export type InternalPropertyDescriptor = {
  name: string,
  value?: RemoteObject,
};

/**
 * Object property descriptor.
 *
 * @type {Object}
 */
export type PropertyDescriptor = {
  name: string,
  value?: RemoteObject,
  writable?: boolean,
  get?: RemoteObject,
  set?: RemoteObject,
  configurable: boolean,
  enumerable: boolean,
  wasThrown?: boolean,
  isOwn?: boolean,
  symbol?: RemoteObject,
};

/**
 * @type {Object}
 */
export type Runtime = {
  /**
   * Evaluates expression on global object.
   *
   * @param {Object}
   * @return {Promise}
   */
  evaluate(arg: {
    expression: string,
    objectGroup?: string,
    includeCommandLineAPI?: boolean,
    silent?: boolean,
    contextId?: ExecutionContextId,
    returnByValue?: boolean,
    generatePreview?: boolean,
    userGesture?: boolean,
    awaitPromise?: boolean
  }): Promise<{ result: RemoteObject, exceptionDetails?: ExceptionDetails }>;

  /**
   * Add handler to promise with given promise object id.
   *
   * @param {Object}
   * @return {Promise}
   */
  awaitPromise(arg: {
    promiseObjectId: RemoteObjectId,
    returnByValue?: boolean,
    generatePreview?: boolean
  }): Promise<{ result: RemoteObject, exceptionDetails?: ExceptionDetails }>;

  /**
   * Calls function with given declaration on the given object.
   * Object group of the result is inherited from the target object.
   *
   * @param {Object}
   * @return {Promise}
   */
  callFunctionOn(arg: {
    objectId: RemoteObjectId,
    functionDeclaration: string,
    arguments?: Array<CallArgument>,
    silent?: boolean,
    returnByValue?: boolean,
    generatePreview?: boolean,
    userGesture?: boolean,
    awaitPromise?: boolean,
  }): Promise<{ result: RemoteObject, exceptionDetails?: ExceptionDetails }>;

  /**
   * Returns properties of a given object.
   * Object group of the result is inherited from the target object.
   *
   * @param {Object}
   * @return {Promise}
   */
  getProperties(arg: {
    objectId: RemoteObjectId,
    ownProperties?: boolean,
    accessorPropertiesOnly?: boolean,
    generatePreview?: boolean
  }): Promise<{
    result: Array<PropertyDescriptor>,
    internalProperties?: Array<InternalPropertyDescriptor>,
    exceptionDetails?: ExceptionDetails
  }>;

  /**
   * Releases remote object with given id.
   *
   * @param {Object}
   * @return {Promise}
   */
  releaseObject(arg: { objectid: RemoteObjectId }): Promise<>;

  /**
   * Releases all remote objects that belong to a given group.
   *
   * @param {Object}
   * @return {Promise}
   */
  releaseObjectGroup(arg: { objectGroup: string }): Promise<>;

  /**
   * Tells inspected instance to run if it was waiting for debugger to attach.
   *
   * @return {Promise}
   */
  runIfWaitingForDebugger(): Promise<>;

  /**
   * Enables reporting of execution contexts creation by means of executionContextCreated event.
   * When the reporting gets enabled
   * the event will be sent immediately for each existing execution context.
   *
   * @return {Promise}
   */
  enable(): Promise<>;

  /**
   * Disables reporting of execution contexts creation.
   *
   * @return {Promise}
   */
  disable(): Promise<>;

  /**
   * Discards collected exceptions and console API calls.
   *
   * @return {Promise}
   */
  discardConsoleEntries(): Promise<>;

  /**
   * @param {Object}
   * @return {Promise}
   */
  setCustomObjectFormatterEnabled(arg: { enabled: boolean }): Promise<>;

  /**
   * Compiles expression.
   *
   * @param {Object}
   * @return {Promise}
   */
  compileScript(arg: {
    expression: string,
    sourceURL: string,
    persistScript: boolean,
    executionContextId?: ExecutionContextId
  }): Promise<{scriptId: ScriptId, exceptionDetails?: ExceptionDetails}>;

  /**
   * Runs script with given id in a given context.
   *
   * @param {Object}
   * @return {Promise}
   */
  runScript(arg: {
    scriptId: ScriptId,
    executionContextId?: ExecutionContextId,
    objectGroup?: string,
    silent?: boolean,
    includeCommandLineAPI?: boolean,
    returnByValue?: boolean,
    generatePreview?: boolean,
    awaitPromise?: boolean
  }): Promise<{
    result: RemoteObject,
    exceptionDetails?: ExceptionDetails
  }>;

  /** events **/

  /**
   * Issued when new execution context is created.
   */
  executionContextCreated(callback: (
    context: ExecutionContextId
  ) => void): void;

  /**
   * Issued when execution context is destroyed.
   */
  executionContextDestroyed(callback: (
    executionContextId: ExecutionContextId
  ) => void): void;

  /**
   * Issued when all executionContexts were cleared in browser
   */
  executionContextsCleared(callback: Function): void;

  /**
   * Issued when exception was thrown and unhandled.
   */
  exceptionThrown(callback: (
    timestamp: Timestamp,
    exceptionDetails: ExceptionDetails
  ) => void): void;

  /**
   * Issued when unhandled exception was revoked.
   */
  exceptionRevoked(callback: (
    reason: string, exceptionId: Number
  ) => void): void;

  /**
   * Issued when console API was called.
   */
 consoleAPICalled(callback: (
   type: ConsoleType,
   args: Array<RemoteObject>,
   executionContextId: ExecutionContextId,
   timestamp: Timestamp,
   stackTrace?: StackTrace,
   context?: string
 ) => void): void;

 /**
  * Issued when object should be inspected
  * (for example, as a result of inspect() command line API call).
  */
 inspectRequested(callback: (
   object: RemoteObject,
   hints: Object
 ) => void): void;
};

// @flow
// from: https://chromedevtools.github.io/devtools-protocol/tot/Security/

/**
 * An internal certificate ID value.
 *
 * @type {Number}
 */
export type CertificateId = Number;

/**
 * The action to take when a certificate error occurs.
 * continue will continue processing the request and cancel will cancel the request.
 *
 * @type {string}
 */
export type CertificateErrorAction = "continue" | "cancel";

/**
 * The security level of a page or resource.
 *
 * @type {string}
 */
export type SecurityState = "unknown" | "neutral" | "insecure" | "warning" | "secure" | "info";

/**
 * An explanation of an factor contributing to the security state.
 *
 * @type {Object}
 */
export type SecurityStateExplanation = {
  securityState: SecurityState,
  summary: string,
  description: string,
  hasCertificate: boolean,
};

/**
 * Information about insecure content on the page.
 *
 * @type {Object}
 */
export type InsecureContentStatus = {
  ranMixedContent: boolean,
  displayedMixedContent: boolean,
  containedMixedForm: boolean,
  ranContentWithCertErrors: boolean,
  displayedContentWithCertErrors: boolean,
  ranInsecureContentStyle: SecurityState,
  displayedInsecureContentStyle: SecurityState,
};

/**
 * @type {Object}
 */
export type Security = {
  /**
   * Enables tracking security state changes.
   *
   * @return {Promise}
   */
  enable(): Promise<>;

  /**
   * Disables tracking security state changes.
   *
   * @return {Promise}
   */
  disable(): Promise<>;

  /**
   * Displays native dialog with the certificate details.
   *
   * @return {Promise}
   */
  showCertificateViewer(): Promise<>;

  /**
   * Handles a certificate error that fired a certificateError event.
   */
  handleCertificateError(arg: {
    eventId: Number, action: CertificateErrorAction
  }): Promise<>;

  /**
   * Enable/disable overriding certificate errors. If enabled,
   * all certificate error events need to be handled by the DevTools client and
   * should be answered with handleCertificateError commands.
   *
   * @param {Object}
   * @return {Promise}
   */
  setOverrideCertificateErrors(arg: { override: boolean }): Promise<>;

  /* events */

  /**
   * The security state of the page changed.
   *
   * @param {Function}
   */
  securityStateChanged(callback: (
    securityState: SecurityState,
    schemeIsCryptographic: boolean,
    explanations: Array<SecurityStateExplanation>,
    insecureContentStatus: InsecureContentStatus,
    summary?: string
  ) => void): void;

  /**
   * There is a certificate error.
   * If overriding certificate errors is enabled,
   * then it should be handled with the handleCertificateError command.
   * Note: this event does not fire if the certificate error has been allowed internally.
   *
   * @param {Function}
   */
  certificateError(callback: (
    eventId: Number, errorType: string, requestURL: string
  ) => void): void;
};

// @flow
// from: https://chromedevtools.github.io/devtools-protocol/tot/Target/

/**
 * @type {string}
 */
export type TargetID = string;

/**
 * @type {string}
 */
export type BrowserContextID = string;

/**
 * @type {Object}
 */
export type RemoteLocation = {
  host: string,
  port: Number,
};

/**
 * @type {Object}
 */
export type TargetInfo = {
  targetId: TargetID,
  type: string,
  title: string,
  url: string,
};

/**
 * @type {Object}
 */
export type Target = {
  /**
   * Controls whether to discover available targets and
   * notify via targetCreated/targetDestroyed events.
   *
   * @param {Object}
   * @return {Promise}
   */
  setDiscoverTargets(arg: { discover: boolean }): Promise<>;

  /**
   * Controls whether to automatically attach to
   * new targets which are considered to be related to this one.
   * When turned on, attaches to all existing related targets as well.
   * When turned off, automatically detaches from all currently attached targets.
   *
   * @param {Object}
   * @return {Promise}
   */
  setAutoAttach(arg: {
    authAttach: boolean,
    waitForDebuggerOnStart: boolean
  }): Promise<>;

  /**
   * @param {Object}
   * @return {Promise}
   */
  setAttachToFrames(arg: { value: boolean }): Promise<>;

  /**
   * Enables target discovery for the specified locations, when setDiscoverTargets was set to true.
   *
   * @param {Object}
   * @return {Promise}
   */
  setRemoteLocations(arg: { locations: Array<RemoteLocation> }): Promise<>;

  /**
   * Sends protocol message to the target with given id.
   *
   * @param {Object}
   * @return {Promise}
   */
  sendMessageToTarget(arg: { targetId: TargetID, message: string }): Promise<>;

  /**
   * Returns information about a target.
   *
   * @param {Object}
   * @return {Promise}
   */
  getTargetInfo(arg: { targetId: TargetID }): Promise<{ targetInfo: TargetInfo }>;

  /**
   * Activates (focuses) the target.
   *
   * @param {Object}
   * @return {Promise}
   */
  activateTarget(arg: { targetId: TargetID }): Promise<>;

  /**
   * Closes the target. If the target is a page that gets closed too.
   *
   * @param {Object}
   * @return {Promise}
   */
  closeTarget(arg: { targetId: TargetID }): Promise<{ success: boolean }>;

  /**
   * Attaches to the target with given id.
   *
   * @param {Object}
   * @return {Promise}
   */
  attachToTarget(arg: { targetId: TargetID }): Promise<{ success: boolean }>;

  /**
   * @param {Object}
   * @return {Promise}
   */
  detachFromTarget(arg: { targetId: TargetID }): Promise<>;

  /**
   * Creates a new empty BrowserContext.
   * Similar to an incognito profile but you can have more than one.
   *
   * @return {Promise}
   */
  createBrowserContext(): Promise<{ browserContextId: BrowserContextID }>;

  /**
   * Deletes a BrowserContext, will fail of any open page uses it.
   *
   * @param {Object}
   * @return {Promise}
   */
  disposeBrowserContext(arg: {
    browserContextId: BrowserContextID
  }): Promise<{ success: boolean }>;

  /**
   * Creates a new page.
   *
   * @param {Object}
   * @return {Promise}
   */
  createTarget(arg: {
    url: string,
    width?: Number,
    height?: Number,
    browserContextId?: BrowserContextID
  }): Promise<{ targetId: TargetID }>;

  /**
   * Retrieves a list of available targets.
   *
   * @return {Promise}
   */
  getTargets(): Promise<{ targetInfos: Array<TargetInfo> }>;

  /* events */

  /**
   * Issued when a possible inspection target is created.
   *
   * @param {Function}
   */
  targetCreated(callback: (targetInfo: TargetInfo) => void): void;

  /**
   * Issued when a target is destroyed.
   *
   * @param {Function}
   */
  targetDestroyed(callback: (targetId: TargetID) => void): void;

  /**
   * Issued when attached to target because of auto-attach or attachToTarget command.
   *
   * @param {Function}
   */
  attachedToTarget(callback: (
    targetInfo: TargetInfo, waitingForDebugger: boolean
  ) => void): void;

  /**
   * Issued when detached from target for any reason (including detachFromTarget command).
   *
   * @param {Function}
   */
  detachedFromTarget(callback: (targetId: TargetID) => void): void;

  /**
   * Notifies about new protocol message from attached target.
   *
   * @param {Function}
   */
  receivedMessageFromTarget(callback: (
    targetId: TargetID, message: string
  ) => void): void;
};

